INSERT INTO `pages` VALUES (1,'About','about','{\"time\": 1765295537701, \"blocks\": [{\"id\": \"eY2oeUE215\", \"data\": {\"text\": \"CÁC CỘT MỐC PHÁT TRIỂN CỦA HOÀNG HẢI\", \"level\": 2}, \"type\": \"header\"}, {\"id\": \"fsWZFk7Kuv\", \"data\": {\"items\": [\"Khởi đầu tử tếẤp ủ và nuôi dưỡng giấc mơ. Hai anh em Nguyễn Đức Hoàng và Nguyễn Đức Hà, khi ấy là du học sinh Việt Nam tại Nhật Bản, nhận thấy nhu cầu mua sắm điện thoại của cộng đồng người Việt đang ngày càng tăng. Đặc biệt, những người mới sang Nhật – từ du học sinh đến người lao động – thường gặp khó khăn khi tiếp cận thị trường, không thông thạo tiếng Nhật và dễ bị lừa đảo bởi các giao dịch thiếu uy tín.\", \"Thành lập công tyThành lập Hoàng Hải Mobile. Nhờ uy tín ngày càng lan tỏa trong cộng đồng người Việt, hai anh em chính thức thành lập công ty Hoàng Hải Mobile. Đây là bước đi đánh dấu sự chuyên nghiệp hóa trong kinh doanh và đặt nền móng cho một hành trình phát triển mạnh mẽ sau này.\", \"Tăng trưởng nóngHoàng Hải bước vào thời kỳ tăng trưởng nóng và bắt đầu nghiên cứu để mở rộng ngành nghề kinh doanh cũng như quy mô nhân sự. Những ngành hàng Hoàng Hải nhắm tới và đặt mục tiêu ra mắt trong vòng 3 năm có thể kể đến như Viễn thông, Vận chuyển.\", \"Ra mắt ConnectRa mắt dịch vụ viễn thông Hoàng Hải Connect. Vượt kế hoạch, Hoàng Hải chính thức ra mắt dịch vụ SIM data và WiFi cầm tay với mức giá hợp lý và dịch vụ vượt trội hơn so với thị trường. Đây là cột mốc quan trọng, đưa Hoàng Hải trở thành một trong những thương hiệu dẫn đầu B2C trong ngành viễn thông phục vụ cộng đồng người Việt tại Nhật.\", \"Ra mắt WatchHoàng Hải Watch tiếp tục ghi dấu ấn khi mở rộng sang lĩnh vực kinh doanh đồng hồ Nhật Bản. Chỉ sau 6 tháng ra mắt, doanh thu từ ngành hàng này đã chiếm 10% trong tổng doanh thu, khẳng định sự tin tưởng của khách hàng.\", \"Ra mắt ComputerHoàng Hải tiếp tục mở rộng sang mảng kinh doanh laptop, Hoàng Hải Computer mang đến cho khách hàng những sản phẩm máy tính giá tốt, với chính sách bảo hành vượt trội. Đây là bước tiến mới nhằm đáp ứng tốt hơn nhu cầu của cộng đồng người Việt tại Nhật Bản.\", \"Mở rộng chi nhánhBước ngoặt mở rộng quy mô. Hoàng Hải chính thức khai trương thêm 3 chi nhánh mới tại Saitama, Chiba và Kanagawa, nâng tổng số điểm bán hàng lên mức ấn tượng. Từ năm 2024, Hoàng Hải cũng tích cực tham gia các sự kiện và lễ hội Việt Nam khắp Nhật Bản – nơi Hoàng Hải có cơ hội gặp gỡ, tri ân khách hàng và gắn kết cộng đồng người Việt xa xứ.\", \"Phát triển bền vữngTập trung phát triển vững mạnh với 5 chi nhánh phủ sóng khắp các khu vực trọng điểm. Đồng thời, 4 mảng kinh doanh chủ lực – Mobile, Watch, Connect, Computer – đều giữ vững thị phần và tiếp tục khẳng định vị thế dẫn đầu trong cộng đồng người Việt tại Nhật Bản. Đây là minh chứng rõ rệt cho sự yêu thương và tin tưởng mà khách hàng đã dành cho Hoàng Hải trong suốt chặng đường phát triển.\"], \"style\": \"ordered\"}, \"type\": \"list\"}, {\"id\": \"s1LBPqz9B9\", \"data\": {\"text\": \"GIÁ TRỊ CỐT LÕI: CÂU CHUYỆN HAPPY VÀ TỬ TẾ\", \"level\": 2}, \"type\": \"header\"}, {\"id\": \"shqSfszSYa\", \"data\": {\"text\": \"1. SỐNG TỬ TẾ\"}, \"type\": \"paragraph\"}, {\"id\": \"tdgkdUjC8W\", \"data\": {\"text\": \"Triết lý sống tử tế là kim chỉ nam cho mọi hành động. Hoàng Hải luôn đặt lợi ích khách hàng, đối tác và cộng đồng lên trước, đảm bảo uy tín và sự bền vững trong kinh doanh. Mỗi bước đi đều hướng tới việc xây dựng một cộng đồng người Việt hạnh phúc và an tâm tại Nhật Bản.\"}, \"type\": \"paragraph\"}, {\"id\": \"l-uqjKKLio\", \"data\": {\"text\": \"2. BÌNH ĐẲNG CƠ HỘI PHÁT TRIỂN CON NGƯỜI\"}, \"type\": \"paragraph\"}, {\"id\": \"Fr6WgsFY9n\", \"data\": {\"text\": \"Hoàng Hải tạo môi trường công bằng, khuyến khích học hỏi và phát triển nghề nghiệp rõ ràng. Mỗi cá nhân đều được trao cơ hội để nâng cao năng lực, khám phá tiềm năng và đóng góp giá trị thực cho tổ chức.\"}, \"type\": \"paragraph\"}, {\"id\": \"nnUqzpvzUA\", \"data\": {\"text\": \"3. KHÁT VỌNG KIẾN TẠO GIÁ TRỊ\"}, \"type\": \"paragraph\"}, {\"id\": \"WCE8cQxleO\", \"data\": {\"text\": \"Mỗi nhân sự đều khao khát mang lại giá trị thiết thực cho khách hàng và cộng đồng. Hoàng Hải không chỉ cung cấp sản phẩm, mà còn hướng tới tạo ra cuộc sống tốt đẹp hơn cho cá nhân, gia đình và hình ảnh doanh nghiệp Việt Nam tại Nhật Bản.\"}, \"type\": \"paragraph\"}, {\"id\": \"OTlpHZGRtv\", \"data\": {\"text\": \"4. CHUYÊN NGHIỆP TỪNG VIỆC NHỎ\"}, \"type\": \"paragraph\"}, {\"id\": \"4Bl-rRSSPF\", \"data\": {\"text\": \"Tác phong làm việc chuyên nghiệp và kỷ luật cao, quy trình rõ ràng và tinh thần hỗ trợ lẫn nhau giúp Hoàng Hải luôn mang lại trải nghiệm xuất sắc trong từng chi tiết nhỏ, từ dịch vụ đến hậu mãi.\"}, \"type\": \"paragraph\"}, {\"id\": \"_nJJ70aayM\", \"data\": {\"text\": \"Hoàng Hải Mobile\"}, \"type\": \"paragraph\"}, {\"id\": \"K_HucmkMAk\", \"data\": {\"text\": \"LINH VẬT THƯƠNG HIỆU\", \"level\": 2}, \"type\": \"header\"}, {\"id\": \"XK1w_aYQv-\", \"data\": {\"text\": \"Tôi là HOP HOP.\"}, \"type\": \"paragraph\"}, {\"id\": \"TEkep8iUb5\", \"data\": {\"text\": \"Hop Hop được sinh ra chỉ với một mong muốn đơn giản: giúp đỡ cộng đồng người Việt tại Nhật vượt qua khó khăn trong cuộc sống xa xứ. Cũng như Hoàng Hải, Hop Hop mang trong mình&nbsp;sứ mệnh lắng nghe, thấu hiểu và hành động ngay khi có người cần giúp đỡ.\"}, \"type\": \"paragraph\"}, {\"id\": \"9A0nxY4wDY\", \"data\": {\"text\": \"Là biểu tượng của niềm tin, sự tận tụy và văn hóa kinh doanh tử tế, Hop Hop chính là người bạn đồng hành thầm lặng – mang trong mình tất cả&nbsp;giá trị cốt lõi đã tạo nên một Hoàng Hải được cộng đồng tin yêu suốt 10 năm qua.\"}, \"type\": \"paragraph\"}], \"version\": \"2.31.0\"}',NULL,NULL,NULL,1,'about',1,1,'2025-12-08 10:47:19','2025-12-09 15:52:22'),(2,'Refund Policy','policies/refund-policy','{\"time\": 1765190839757, \"blocks\": [{\"data\": {\"text\": \"Refund & Return Policy\", \"level\": 2}, \"type\": \"header\"}, {\"data\": {\"text\": \"You can request a refund/return within 7 days if the product has a manufacturer defect.\"}, \"type\": \"paragraph\"}], \"version\": \"2.28.2\"}',NULL,NULL,NULL,1,'policy',1,1,'2025-12-08 10:47:19','2025-12-08 10:47:19');
